import React from "react";
import TextInputGroup from "./TextInputGroup";

class AddPublicNumber extends React.Component {
  state = {
    public_number: "",
    name: "",
    error: null,
    errors: {
      public_number: "",
      name: ""
    }
  };

  onChange = e => {
    if (this.state.errors.name !== "" || this.state.errors.public_number !== "") {
      this.setState({
        errors: {
          name: "",
          public_number: ""
        }
      });
    }
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  postDataHandler = (e) => {

    if (this.state.name === "") {
      e.preventDefault();
      this.setState({
        errors: { name: "Name is required" }
      });
      return;
    }

    if (this.state.public_number === "") {
      e.preventDefault();
      this.setState({
        errors: { public_number: "Phone number is required" }
      });
      return;
    }

    //if (!this.state.public_number.match(/^\d{12}$/) && !this.state.public_number.match(/^\d{13}$/)) {
    if (!(/^[358][0-9]/).test(this.state.public_number)) {
      e.preventDefault();
      this.setState({
        errors: { public_number: "Invalid, must have 10 digits" }
      });
      return;
    }

    if (this.state.errors.public_number === "" && this.state.errors.name === "") {
      fetch('http://localhost/addPublicNumber.php', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
        },
        body: JSON.stringify({
          name: this.state.name,
          public_number: this.state.public_number
        })
      }).catch(error => this.setState({ error }));
    } else {
      console.log(this.state.errors);
    }
  }

  render() {
    return (
      <div>
        <div className="ui one column stackable center aligned page grid">
          <div className="column twelve wide"></div>
        </div>
        <hr />
        <div className="ui centered card" style={{ padding: "30px" }}>
          <form className="ui form">
          <div className="required field">
              <TextInputGroup
                type="text"
                value={this.state.name}
                placeholder="Enter Your Name"
                onChange={this.onChange}
                name="name"
                label="Name"
                error={this.state.errors.name}
              />
            </div>
            <TextInputGroup
              type="text"
              value={this.state.public_number}
              placeholder="Enter Your Phone Number"
              onChange={this.onChange}
              name="public_number"
              label="Phone Number"
              error={this.state.errors.public_number}
            />
            <button
              onClick={this.postDataHandler}
              className="ui blue button"
              style={{ marginTop: "10px" }}
            >
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default AddPublicNumber;
