import React, { Component } from 'react'
import { Button, Modal } from 'semantic-ui-react';
import EditShortNumber from './EditShortNumber';

class EditShortNumberModal extends Component {
  state = { open: false };

  open = () => this.setState({ open: true });
  close = () => this.setState({ open: false });

  render() {
    const { open } = this.state;
    return (
      <Modal
        open={open}
        onOpen={this.open}
        onClose={this.close}
        size="small"
        trigger={
          <Button  style={{ paddingRight: '3px', paddingLeft: '11px', width: '38.51px' }}>
            <i aria-hidden="true" className="pencil alternate icon"></i> 
          </Button>
        }
      >
        <Modal.Header>Enter a short number</Modal.Header>
        <Modal.Content>
        <div><EditShortNumber short_number={this.props.short_number} id={this.props.id}/></div>
        </Modal.Content>
      </Modal>
    );
  }
}

export default EditShortNumberModal
