			rejectUnauthorized: false,
			method: 'PUT',
			body: JSON.stringify({
				number_id: 408,
				short_number: "09"
			}),
			headers: {
				"Authorization": "Basic " + base64data,
				"Content-type": "application/json; charset=UTF-8"
			}
		}).then(response => {
						return response.json()
		}).then(json => {
			console.log(json)
			this.setState({
				user:json
			});
		})
	}
	render() {                            
		return (
			<div>

			</div>
		)
	}
}
export default RestController;
