import React, { Component } from 'react'
import AddPublicNumber from './AddPublicNumber';
import AddOrUpdateShortNumber from './AddOrUpdateShortNumber';
import { Button, Modal, Icon } from 'semantic-ui-react';

class AddShortNumberModal extends Component {
  state = { open: false };

  open = () => this.setState({ open: true });
  close = () => this.setState({ open: false });

  render() {
    const { open } = this.state;
    return (
      <Modal
        open={open}
        onOpen={this.open}
        onClose={this.close}
        size="small"
        trigger={
          <Button primary icon>
            Add Short Number <Icon name="right chevron" />
          </Button>
        }
      >
        <Modal.Header>Step #2</Modal.Header>
        <Modal.Content>
        <div><AddOrUpdateShortNumber data={this.props.data}/></div>
        </Modal.Content>
      </Modal>
    );
  }
}

class AddPublicNumberModal extends Component {
  state = { open: false };

  open = () => this.setState({ open: true });
  close = () => this.setState({ open: false });

  render() {
    const { open } = this.state;

    return (
      <Modal
        open={open}
        onOpen={this.open}
        onClose={this.close}
        size="small"
        trigger={
          <Button primary icon>
            Add Public Number <Icon name="right chevron" />
          </Button>
        }
      >
        <Modal.Header>Step #2</Modal.Header>
        <Modal.Content>
          <div><AddPublicNumber /></div>
        </Modal.Content>
      </Modal>
    );
  }
}

const AddNumberModal = (props) => (
  <Modal trigger={<Button>Add Number</Button>}>
    <Modal.Header>Step #1</Modal.Header>
    <Modal.Content image>
      <Modal.Description>
        <p>Please choose an option</p>
      </Modal.Description>
    </Modal.Content>
    <Modal.Actions>
      <AddPublicNumberModal />
      <AddShortNumberModal data={props.data} />
    </Modal.Actions>
  </Modal>
);

export default AddNumberModal
