import React from 'react';

class DeleteNumber extends React.Component {
	constructor(props) {
		super(props);
	}

		componentDidMount() {
		fetch('http://localhost/removeShortNumber.php', {
		  method: 'POST',
		  headers: {	  	
		    'Accept': 'application/json',
		    'Content-Type': 'application/json'
		  },
		  body: JSON.stringify({
		    short_number: '07'
		  })
		})
	}

	render() {                            
		return (
			<div>
				<p><b>Resource deleted in the server</b></p>
			</div>
		)
	}
}

export default DeleteNumber;
