import React from 'react';
import { Table } from 'semantic-ui-react';
import './PhoneList.css';
import { Button, Modal } from 'semantic-ui-react';
import EditNameModal from './EditNameModal';

class PhoneList extends React.Component {

	state = { openShortNumber: false, openPublicNumber: false, id: '' }

	openShortNumber = () => this.setState({ openShortNumber: true });
	closeShortNumber = () => this.setState({ openShortNumber: false });

	openPublicNumber = () => this.setState({ openPublicNumber: true });
	closePublicNumber = () => this.setState({ openPublicNumber: false });

	deleteShortNumberModal = (e) => {
		this.openShortNumber();
		this.setState({id: e.target.id});	
	}

	deletePublicNumberModal = (e) => {
		this.openPublicNumber();
		this.setState({id: e.target.id});	
	}

	deleteShortNumberHandler = () => {
		this.props.onSubmitSN(this.state.id);
		this.closeShortNumber();
	}

	deletePublicNumberHandler = (e) => {
		this.props.onSubmitPN(this.state.id);
		this.closePublicNumber();
	}

	render() {

		const { openShortNumber } = this.state;
		const { openPublicNumber } = this.state;

		const cells = this.props.data.map(item => {
			return (
				<Table.Row key={item.id}>
					<Table.Cell textAlign='center' style={{ width: '10%', border: '1px solid #ccc', borderRightColor: 'transparent' }}>{item.info}</Table.Cell>
					<Table.Cell textAlign='center' style={{ width: '10%', border: '1px solid #ccc' }}><EditNameModal data={this.props.data} /></Table.Cell>
					<Table.Cell textAlign='center' style={{ width: '30%', border: '1px solid #ccc', borderRightColor: 'transparent' }}>{item.number}</Table.Cell>
					<Table.Cell textAlign='center' style={{ width: '10%', border: '1px solid #ccc' }}><button id={item.id} onClick={this.deletePublicNumberModal} className="ui icon button"><i aria-hidden="true" className="trash alternate icon"></i></button></Table.Cell>
					<Table.Cell textAlign='center' style={{ width: '20%', border: '1px solid #ccc', borderRightColor: 'transparent' }}>{item.short_number}</Table.Cell>
					<Table.Cell textAlign='center' style={{ width: '10%', border: '1px solid #ccc' }}>{item.short_number !== undefined ? <button id={item.short_number} onClick={this.deleteShortNumberModal} className="ui icon button"><i aria-hidden="true" className="trash alternate icon"></i></button> : null}</Table.Cell>
				</Table.Row>
			);
		});
		//celled
		//collapsing
		return (
			<div style={{ marginLeft: '20%', marginRight: '20%' }}>
				<Table celled structured>
					<Table.Header>
						<Table.Row>
							<Table.HeaderCell textAlign='center' style={{ border: '1px solid #ccc' }} colSpan='2'>Name</Table.HeaderCell>
							<Table.HeaderCell textAlign='center' style={{ border: '1px solid #ccc' }} colSpan='2'>Public Number</Table.HeaderCell>
							<Table.HeaderCell textAlign='center' style={{ border: '1px solid #ccc' }} colSpan='2'>Short Number</Table.HeaderCell>
						</Table.Row>
					</Table.Header>
					<Table.Body>
						{cells}
					</Table.Body>
				</Table>
				<Modal
					open={openShortNumber}
					onOpen={this.openShortNumber}
					onClose={this.closeShortNumber}
				>
					<Modal.Header>Delete Your Number</Modal.Header>
					<Modal.Content>
						<p>Are you sure you want to delete this number</p>
					</Modal.Content>
					<Modal.Actions>
						<Button onClick={this.closeShortNumber} negative>
							No
            			</Button>
						<Button
							onClick={this.deleteShortNumberHandler}
							positive
							labelPosition='right'
							icon='checkmark'
							content='Yes'
						/>
					</Modal.Actions>
				</Modal>
				<Modal
					open={openPublicNumber}
					onOpen={this.openPublicNumber}
					onClose={this.closePublicNumber}
				>
					<Modal.Header>Delete Your Number</Modal.Header>
					<Modal.Content>
						<p>Are you sure you want to delete this number</p>
					</Modal.Content>
					<Modal.Actions>
						<Button onClick={this.closePublicNumber} negative>
							No
            			</Button>
						<Button
							onClick={this.deletePublicNumberHandler}
							positive
							labelPosition='right'
							icon='checkmark'
							content='Yes'
						/>
					</Modal.Actions>
				</Modal>
			</div>
		);
	}
};

export default PhoneList;

