import React from 'react';
import TextInputGroup from "./TextInputGroup";

class AddOrUpdateShortNumber extends React.Component {

	state = {
		value: '',
		id: '',
		short_number: '',
		errors: {
			value: '',
			short_number: ""
		}
	};

	onChange = e => {

		if (this.state.errors.value !== "" || this.state.errors.short_number !== "") {
			this.setState({
				errors: {
					value: "",
					short_number: ""
				}
			});
		}

		this.setState({ value: e.target.value, id: e.target.options[e.target.selectedIndex].getAttribute('id') });
	};

	postDataHandler = (e) => {

		const value = this.state.value;
		const short_number = this.state.short_number;

		if (value === "select") {
			e.preventDefault();
			this.setState({
				errors: { value: "Please select a number" }
			});
			return;
		}

		if (short_number === "") {
			e.preventDefault();
			this.setState({
				errors: { short_number: "Short number is required" }
			});
			return;
		}

		if (short_number <= 0 && !Number.isInteger(short_number)) {
			this.setState({
				errors: { short_number: "Phone number is invalid" }
			});
			return;
		}

		if (this.state.errors.short_number === "" && this.state.errors.value === "") {
			fetch('http://localhost/addOrUpdateShortNumber.php', {
				method: 'POST',
				headers: {
					'Accept': 'application/json',
				},
				body: JSON.stringify({
					id: this.state.id,
					short_number: this.state.short_number
				})
			})
		} else {
			console.log(this.state.errors);
		}	
	}

	render() {

		const
			modifedData = [...this.props.data],
			keys = ['number'],
			filtered = modifedData.filter(
				(s => o =>
					(k => !s.has(k) && s.add(k))
						(keys.map(k => o[k]).join('|'))
				)
					(new Set)
			);

		return (
			<div>
				<div className="ui one column stackable center aligned page grid">
					<div className="column twelve wide"></div>
				</div>
				<hr />
				<div className="ui centered card" style={{ padding: "30px" }}>
					<form className="ui form">
						<div style={{ marginBottom: '10px' }}><strong>Public number</strong></div>
						<div style={{ marginBottom: '10px' }} >
							<select value={this.state.value}
								onChange={this.onChange}>
								<option value="select">Select a number</option>
								{filtered.map((number) => <option key={number.id} id={number.id} value={number.number}>{number.number}</option>)}
							</select>
						</div>
						<TextInputGroup
							type="text"
							value={this.state.short_number}
							placeholder="Enter a short number"
							onChange={(event) => this.setState({ short_number: event.target.value })}
							name="short_number"
							label="Short Number"
							error={this.state.errors.short_number}
						/>
						<button
							onClick={this.postDataHandler}
							className="ui blue button"
							style={{ marginTop: "10px" }} >
							Submit
						</button>
					</form>
				</div>
			</div>
		);
	}
}
export default AddOrUpdateShortNumber;
