import React from 'react';
import PhoneList from './PhoneList';
import AddPublicNumber from './AddPublicNumber';
import Demo from './Demo';
import AddNumberModal from './AddNumberModal';
import { Button, Header, Image, Modal } from 'semantic-ui-react';


class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = { shortNumbers: [] };
	}

	componentDidMount() {
		fetch("http://localhost/post.php")
			.then(response => {
				return response.json();
			}).then(result => {
				this.setState({
					shortNumbers: result
				});
			});
	}

	render() {
		return (
			<div style={{ margin: '50px' }}>
				<div style={{ marginTop: '30px', marginBottom: '30px' }} className="ui center aligned grid">

				</div>
				<div><PhoneList shortNumbers={this.state.shortNumbers} /></div>
			</div>
		)
	}
}
export default App;
