import React, { Component } from 'react'
import AddPublicNumber from './AddPublicNumber';
import AddOrUpdateShortNumber from './AddOrUpdateShortNumber';
import PhoneList from './PhoneList';
import { Button, Header, Image, Modal, Icon } from 'semantic-ui-react';

class AddShortNumberModal extends Component {
  state = { open: false };

  open = () => this.setState({ open: true });
  close = () => this.setState({ open: false });

  render() {
    const { open } = this.state;
    return (
      <Modal
        open={open}
        onOpen={this.open}
        onClose={this.close}
        size="small"
        trigger={
          <Button primary icon>
            AddShortNumber <Icon name="right chevron" />
          </Button>
        }
      >
        <Modal.Header>Modal #2</Modal.Header>
        <Modal.Content>
        <div><AddOrUpdateShortNumber shortNumbers={this.props.shortNumbers}/></div>
        </Modal.Content>
      </Modal>
    );
  }
}

class AddPublicNumberModal extends Component {
  state = { open: false };

  open = () => this.setState({ open: true });
  close = () => this.setState({ open: false });

  render() {
    const { open } = this.state;

    return (
      <Modal
        open={open}
        onOpen={this.open}
        onClose={this.close}
        size="small"
        trigger={
          <Button primary icon>
            AddPublicNumber <Icon name="right chevron" />
          </Button>
        }
      >
        <Modal.Header>Modal #2</Modal.Header>
        <Modal.Content>
          <div><AddPublicNumber /></div>
        </Modal.Content>
      </Modal>
    );
  }
}

const AddNumberModal = (props) => (
  <Modal trigger={<Button>Multiple Modals</Button>}>
    <Modal.Header>Modal #1</Modal.Header>
    <Modal.Content image>
      <Modal.Description>
        <p>We have more to share with you. Follow us along to modal 2</p>
      </Modal.Description>
    </Modal.Content>
    <Modal.Actions>
      <AddPublicNumberModal />
      <AddShortNumberModal shortNumbers={props.shortNumbers} />
    </Modal.Actions>
  </Modal>
);

export default AddNumberModal
