import React, { Component } from "react";
import { connect } from "react-redux";
import "./Auth.css";
import * as actions from "../store/actions/index";
import Spinner from "../components/UI/Spinner/Spinner";

class Auth extends Component {
  state = {
    email: "",
    password: "",
    submitted: false,
    error: ""
  };

  handleChange = e => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  handleSubmit = e => {
    e.preventDefault();

    this.setState({ submitted: true });
    const { email, password } = this.state;

    // stop here if form is invalid
    if (!(email && password)) {
      return;
    }

    this.props.onAuth(this.state.email, this.state.password);
  };

  render() {
    const { email, password, submitted, error } = this.state;

    let errorMessage = null;

    if (this.props.error) {
      errorMessage = <p>{this.props.error.message}</p>;
    }

    let form = (
      <div>
        <div className="page-login" id="logBox">
          <div className="ui centered grid container">
            <div className="nine wide column">
              <div className="ui fluid card">
                <div className="content">
                  <form className="ui form" onSubmit={this.handleSubmit}>
                    <div
                      className={
                        "field" + (submitted && !email ? " has-error" : "")
                      }
                    >
                      <label htmlFor="email">Email</label>
                      <input
                        type="text"
                        className="form-control"
                        name="email"
                        value={email}
                        onChange={this.handleChange}
                      />
                      {submitted && !email && (
                        <div
                          style={{
                            textAlign: "center",
                            paddingTop: "8px",
                            color: "red"
                          }}
                        >
                          Email is required
                        </div>
                      )}
                    </div>
                    <div
                      className={
                        "field" + (submitted && !password ? " has-error" : "")
                      }
                    >
                      <label htmlFor="password">Password</label>
                      <input
                        type="password"
                        className="form-control"
                        name="password"
                        value={password}
                        onChange={this.handleChange}
                      />
                      {submitted && !password && (
                        <div style={{ paddingTop: "8px", color: "red" }}>
                          Password is required
                        </div>
                      )}
                    </div>
                    <button
                      style={{ margin: "0 auto", display: "block" }}
                      className="ui primary labeled icon button"
                      type="submit"
                    >
                      <i className="unlock alternate icon" />
                      Login
                    </button>

                    {errorMessage && (
                      <div
                        style={{
                          paddingTop: "8px",
                          color: "red",
                          fontSize: "small"
                        }}
                      >
                        {errorMessage}
                      </div>
                    )}
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );

    if (this.props.loading) {
      form = <Spinner />;
    }

    return <div>{form}</div>;
  }
}

const mapStateToProps = state => {
  return {
    loading: state.auth.loading,
    error: state.auth.error
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onAuth: (email, password) => dispatch(actions.auth(email, password))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Auth);
