import React from 'react';
import PhoneList from './PhoneList';
import AddNumberModal from './AddNumberModal';
import { Menu } from 'semantic-ui-react'

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {activeItem: 'home', id: 1, data: [] };
	}

	componentDidMount() {
		fetch("http://localhost/data.php")
			.then(response => {
				return response.json();
			}).then(result => {
				this.setState({
					data: result
				});
			});
	}

	/*
	componentDidUpdate(prevProps, prevState) {
		console.log(prevState.id);
		console.log(this.state.id);
		if (prevState.id !== this.state.id) {
			fetch("http://localhost/data.php")
				.then(response => {
					return response.json();
				}).then(result => {
					this.setState({
						data: result
					});
				});
		}
	}
	*/

	deleteShortNumberHandler = (id) => {
		this.setState({ id: id });
		console.log(this.state.id);
		this.forceUpdate;
		/*
		fetch('http://localhost/removeShortNumber.php', {
			method: 'POST',
			headers: {
				'Accept': 'application/json',
			},
			body: JSON.stringify({
				short_number: id
			})
		});
		*/
	}

	deletePublicNumberHandler = (id) => {
		this.setState({ id: id });
		fetch('http://localhost/removePublicNumber.php', {
			method: 'POST',
			headers: {
				'Accept': 'application/json',
			},
			body: JSON.stringify({
				number_id: id
			})
		})
	}

	handleItemClick = (e, { name }) => this.setState({ activeItem: name })

	render() {

		const { activeItem } = this.state

		return (
			<div>
				<Menu stackable size='massive' inverted>
					<Menu.Item >
						<img className="ui small image" src='http://www.nsftele.com/sites/default/files/logo_2_vari.png' />
					</Menu.Item>
					<Menu.Item name='home' active={activeItem === 'home'} onClick={this.handleItemClick} />
					<Menu.Item name='service areas' active={activeItem === 'service areas'} onClick={this.handleItemClick} />
					<Menu.Item name='customers' active={activeItem === 'customers'} onClick={this.handleItemClick} />
					<Menu.Item name='company' active={activeItem === 'company'} onClick={this.handleItemClick} />
					<Menu.Item name='contact us' active={activeItem === 'contact us'} onClick={this.handleItemClick} />
				</Menu>
				<div style={{ marginTop: '30px', marginBottom: '30px' }} className="ui center aligned grid">
					<AddNumberModal data={this.state.data} />
				</div>
				<div><PhoneList onSubmitPN={this.deletePublicNumberHandler} onSubmitSN={this.deleteShortNumberHandler} data={this.state.data} /></div>
			</div>
		)
	}
}
export default App;
