import React from "react";
import TextInputGroup from "./TextInputGroup";

class AddPublicNumber extends React.Component {
  state = {
    name: '',
    public_number: '',
    errors: {
      name: '',
      public_number: ""
    }
  };

  onChange = e => {
    if (this.state.errors.name !== "" || this.state.errors.phone_no !== "") {
      this.setState({
        errors: {
          name: "",
          phone_no: ""
        }
      });
    }
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  postDataHandler = () => {
    fetch('http://localhost/addPublicNumber.php', {
      method: 'POST',
      headers: {	  	
        'Accept': 'application/json',
      },
      body: JSON.stringify({
        public_number: this.state.public_number
      })
    })
	}

  render() {
    return (
      <div>
        <div className="ui one column stackable center aligned page grid">
          <div className="column twelve wide"></div>
        </div>
        <hr />
        <div className="ui centered card" style={{ padding: "30px" }}>
          <form className="ui form">
            <TextInputGroup
              type="text"
              value={this.state.public_number}
              placeholder="Enter Your Phone Number"
              onChange={(event) => this.setState({public_number: event.target.value})}
              name="public_number"
              label="Phone Number"
              error={this.state.errors.public_number}
            />
            <button
              onClick={this.postDataHandler}
              className="ui blue button"
              style={{ marginTop: "10px" }}
            >
              Submit
            </button>
          </form>
        </div>
        {this.state.public_number ? (
          <div>
            <hr />
            <h3>Subscriber To Be Added: </h3>
            <p>Phone Number: {this.state.public_number}</p>
          </div>
        ) : null}
      </div>
    );
  }
}

export default AddPublicNumber;
