import React from "react";
import TextInputGroup from "./TextInputGroup";

class AddPublicNumber extends React.Component {
  state = {
    update: "",
    phone_no: '',
    errors: {
      phone_no: ""
    }
  };

  postDataHandler = () => {
    console.log(this.state.phone_no);
    fetch('http://localhost/addPublicNumber.php', {
      method: 'POST',
      headers: {	  	
        'Accept': 'application/json',
      },
      body: JSON.stringify({
        public_number: this.state.phone_no
      })
    })
	}

  render() {
    return (
      <div>
        <div className="ui one column stackable center aligned page grid">
          <div className="column twelve wide">
          {/*
            <Link to="/" className="ui large positive button">
              <i className="fas fa-chevron-circle-left" /> Go Back
            </Link>
          */}
          </div>
        </div>
        <hr />
        <div className="ui centered card" style={{ padding: "30px" }}>
          <form className="ui form">
            <TextInputGroup
              type="text"
              value={this.state.phone_no}
              placeholder="Enter Your Phone Number"
              onChange={(event) => this.setState({phone_no: event.target.value})}
              name="phone_no"
              label="Phone Number"
              error={this.state.errors.phone_no}
            />
            <button
              onClick={this.postDataHandler}
              className="ui blue button"
              style={{ marginTop: "10px" }}
            >
              Submit
            </button>
          </form>
        </div>
        {this.state.phone_no ? (
          <div>
            <hr />
            <h3>Subscriber To Be Added: </h3>
            <p>Phone Number: {this.state.phone_no}</p>
          </div>
        ) : null}
      </div>
    );
  }
}

export default AddPublicNumber;


