import React, { Component } from "react";
import { connect } from "react-redux";

import Button from "../components/UI/Button/Button";
import Spinner from "../components/UI/Spinner/Spinner";
import "./Auth.css";
import Input from "../components/UI/Input/Input";

class Auth extends Component {
  state = {
    username: "",
    password: "",
    submitted: false,
    loading: false,
    error: ""
  };

  handleChange = e => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  handleSubmit = e => {
    e.preventDefault();

    this.setState({ submitted: true });
    const { username, password } = this.state;

    // stop here if form is invalid
    if (!(username && password)) {
      return;
    }

    this.setState({ loading: true });
  };

  render() {
    const { username, password, submitted, error } = this.state;
    return (
      <div>
        <div className="page-login" id="logBox">
          <div className="ui centered grid container">
            <div className="nine wide column">
              <div className="ui fluid card">
                <div className="content">
                  <form className="ui form" onSubmit={this.handleSubmit}>
                    <div
                      className={
                        "field" + (submitted && !username ? " has-error" : "")
                      }
                    >
                      <label htmlFor="username">Username</label>
                      <input
                        type="text"
                        className="form-control"
                        name="username"
                        value={username}
                        onChange={this.handleChange}
                      />
                      {submitted && !username && (
                        <div style={{ paddingTop: "8px", color: "red" }}>
                          Username is required
                        </div>
                      )}
                    </div>
                    <div
                      className={
                        "field" + (submitted && !password ? " has-error" : "")
                      }
                    >
                      <label htmlFor="password">Password</label>
                      <input
                        type="password"
                        className="form-control"
                        name="password"
                        value={password}
                        onChange={this.handleChange}
                      />
                      {submitted && !password && (
                        <div style={{ paddingTop: "8px", color: "red" }}>
                          Password is required
                        </div>
                      )}
                    </div>
                    <button
                      style={{ margin: "0 auto", display: "block" }}
                      className="ui primary labeled icon button"
                      type="submit"
                    >
                      <i className="unlock alternate icon" />
                      Login
                    </button>
                    {error && (
                      <div style={{ paddingTop: "8px", color: "red" }}>
                        {error}
                      </div>
                    )}
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Auth;
