import React from 'react';
import PhoneList from './PhoneList';
import AddNumberModal from '../UI/Modals/AddNumberModal';
import TableForPrint from './TableForPrint';
import XLSX from 'xlsx';
import NavBar from '../UI/NavBar';

class HomePage extends React.Component {
  state = {
    data: [],
    error: null,
    fileName: 'contact.xlsx',
    alarmMessage: ''
  };

  customers = () => {
    let customers = this.state.data.map(
      ({
        alert_info_enabled,
        aservicemask_id,
        forward_autoself,
        number,
        language_id,
        notify,
        operator_id,
        number_id,
        id,
        type,
        use_default,
        voiplocation_code,
        company_id,
        whitelist,
        whitelist_mode,
        whitelist_pin_in,
        whitelist_pin_in_enabled,
        whitelist_pin_out,
        whitelist_pin_out_enabled,
        ...item
      }) => item
    );
    customers = customers.map(({ info, short_number }) => ({
      Name: info,
      Number: short_number
    }));
    customers = customers.filter(
      person => person.Name !== 'Opas test IVR number'
    );
    customers = customers.filter(person => person.Name !== 'Call Conference');
    customers.push({ Name: 'Call Conference', Number: '99' });
    customers.push({ Name: 'Palvelu Numero', Number: '034439430' });
    return customers;
  };

  printData = () => {
    const divToPrint = document.getElementById('printTable');
    const newWin = window.open('');
    newWin.document.write(divToPrint.outerHTML);
    newWin.print();
    newWin.close();
  };

  exportFile = () => {
    /* convert state.data to worksheet */
    const ws = XLSX.utils.json_to_sheet(this.customers());
    /* build a workbook */
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'SheetJS');
    /* generate XLSX file and send to client */
    XLSX.writeFile(wb, 'contact.xlsx');
  };

  loadData() {
    fetch('http://demo2.nsftele.com/travel-guide-php/data.php')
      .then(response => {
        return response.json();
      })
      .then(result => {
        this.setState({
          data: result
        });
      })
      .catch(error => this.setState({ error }));
  }

  componentDidMount() {
    this.loadData();
  }

  deleteShortNumberHandler = id => {
    fetch('http://demo2.nsftele.com/travel-guide-php/removeShortNumber.php', {
      method: 'POST',
      headers: {
        Accept: 'application/json'
      },
      body: JSON.stringify({
        short_number: id
      })
    })
      .then(() => {
        this.loadData();
      })
      .catch(error => this.setState({ error }));
  };

  deletePublicNumberHandler = id => {
    fetch('http://demo2.nsftele.com/travel-guide-php/removePublicNumber.php', {
      method: 'POST',
      headers: {
        Accept: 'application/json'
      },
      body: JSON.stringify({
        number_id: id
      })
    })
      .then(() => {
        this.loadData();
      })
      .catch(error => this.setState({ error }));
  };

  makeAlarmCall = () => {
    this.refs.child.alarmCallAPI();
  };

  showAddNumberModal = () => {
    this.refs.child2.handleShowModal();
  };

  showLog = () => {
    this.refs.child.showLog();
  };

  onChangeMessage = e => {
    this.setState({ alarmMessage: e.target.value });
  };

  render() {
    return (
      <div style={{ paddingBottom: '100px' }}>
        <NavBar />
        {this.state.error ? (
          <div>
            <p>Network error: {this.state.error.message}</p>
          </div>
        ) : (
          <div>
            <div
              className="btn-toolbar mb-3 "
              role="toolbar"
              aria-label="Toolbar with button groups"
              style={{ paddingLeft: '50px', paddingTop: '20px' }}
            >
              <div
                class="btn-group mr-2 btn-group-sm"
                role="group"
                aria-label="First group"
              >
                <button
                  className="btn btn-outline-secondary"
                  onClick={this.exportFile}
                >
                  <i className="fa fa-download"></i> Export To Excel
                </button>
                <button
                  className="btn btn-outline-secondary"
                  onClick={this.printData}
                >
                  <i className="fa fa-print"></i> Print Table
                </button>
                <button
                  onClick={this.showAddNumberModal}
                  type="button"
                  className="btn btn-outline-secondary"
                >
                  <i className="fa fa-address-book"></i> Add Number
                </button>
              </div>
            </div>
            <div
              style={{
                paddingLeft: '50px',
                paddingTop: '20px',
                marginRight: '10%'
              }}
            >
              <div class="input-group mb-3">
                <div class="input-group-prepend" id="button-addon3">
                  <button
                    onClick={this.makeAlarmCall}
                    class="btn btn-outline-secondary"
                    type="button"
                  >
                    <i className="fa fa-phone-square"></i> Make Alarm Call
                  </button>
                  <button
                    onClick={this.showLog}
                    class="btn btn-outline-secondary"
                    type="button"
                  >
                    <i className="fa fa-history"></i> Call Log
                  </button>
                </div>
                <input
                  onChange={this.onChangeMessage}
                  type="text"
                  value={this.state.alarmMessage}
                  className="form-control"
                  placeholder="Enter call alarm message"
                  name="name"
                />
              </div>
            </div>
            <div
              style={{
                fontSize: 'large',
                paddingLeft: '20px',
                paddingTop: '10px'
              }}
            >
              <br />
              Palvelu Numero: 034439430
            </div>
            <div style={{ paddingTop: '30px' }}>
              <PhoneList
                ref="child"
                onSubmitPN={this.deletePublicNumberHandler}
                onSubmitSN={this.deleteShortNumberHandler}
                data={this.state.data}
                message={this.state.alarmMessage}
              />
            </div>
            <div>
              <TableForPrint data={this.state.data} />
            </div>
            <div>
              <AddNumberModal ref="child2" data={this.state.data} />
            </div>
          </div>
        )}
      </div>
    );
  }
}
export default HomePage;
