import React from 'react';
import './PhoneList.css';
import EditNameModal from './EditNameModal';
import EditShortNumberModal from './EditShortNumberModal';
import { Button, Modal } from 'react-bootstrap';

class PhoneList extends React.Component {
  state = {
    openShortNumber: false,
    openPublicNumber: false,
    id: '',
    alarmNumbers: []
  };

  openShortNumber = () => this.setState({ openShortNumber: true });
  closeShortNumber = () => this.setState({ openShortNumber: false });

  openPublicNumber = () => this.setState({ openPublicNumber: true });
  closePublicNumber = () => this.setState({ openPublicNumber: false });

  deleteShortNumberModal = e => {
    this.openShortNumber();
    this.setState({ id: e.target.id });
  };

  deletePublicNumberModal = e => {
    this.openPublicNumber();
    this.setState({ id: e.target.id });
  };

  deleteShortNumberHandler = () => {
    this.props.onSubmitSN(this.state.id);
    this.closeShortNumber();
  };

  deletePublicNumberHandler = e => {
    this.props.onSubmitPN(this.state.id);
    this.closePublicNumber();
  };

  checkboxHandler = e => {
    console.log(this.state.alarmNumbers);
    if (!this.state.alarmNumbers.includes(e.target.value)) {
      const oldList = [...this.state.alarmNumbers];
      oldList.push(e.target.value);
      this.setState({ alarmNumbers: oldList }, function() {
        console.log(this.state.alarmNumbers);
      });
    } else {
      const oldList = [...this.state.alarmNumbers];
      oldList.pop(e.target.value);
      this.setState({ alarmNumbers: oldList }, function() {
        console.log(this.state.alarmNumbers);
      });
    }
  };

  render() {
    const cells = this.props.data.map((item, index) => {
      if (item.number !== '35834439430' && item.number !== '35834439431') {
        return (
          <tr key={index}>
            <td
              className="align-middle"
              style={{
                width: '10%',
                border: '1px solid #ccc',
                borderRightColor: 'transparent'
              }}
            >
              {item.info}
            </td>
            <td
              className="text-center align-middle"
              style={{ width: '10%', border: '1px solid #ccc' }}
            >
              <EditNameModal id={item.id} />
            </td>
            <td
              className="text-center align-middle"
              style={{
                width: '30%',
                border: '1px solid #ccc',
                borderRightColor: 'transparent'
              }}
            >
              {item.number}
            </td>
            <td
              className="text-center align-middle"
              style={{ width: '10%', border: '1px solid #ccc' }}
            >
              <button
                id={item.id}
                onClick={this.deletePublicNumberModal}
                className="btn bg-transparent"
              >
                <i className="fa fa-trash" aria-hidden="true"></i>
              </button>
            </td>
            <td
              className="text-center align-middle"
              style={{
                width: '5%',
                border: '1px solid #ccc',
                borderRightColor: 'transparent'
              }}
            >
              {item.short_number}
            </td>
            <td
              className="text-center align-middle"
              style={{
                width: '10%',
                border: '1px solid #ccc',
                borderRightColor: 'transparent'
              }}
            >
              {item.short_number !== undefined ? (
                <EditShortNumberModal
                  short_number={item.short_number}
                  id={item.id}
                />
              ) : null}
            </td>
            <td
              className="text-center align-middle"
              style={{ width: '10%', border: '1px solid #ccc' }}
            >
              {item.short_number !== undefined ? (
                <button
                  id={item.short_number}
                  onClick={this.deleteShortNumberModal}
                  className="btn bg-transparent"
                >
                  <i className="fa fa-trash" aria-hidden="true"></i>
                </button>
              ) : null}
            </td>
            <td
              className="text-center align-middle"
              style={{
                width: '10%',
                border: '1px solid #ccc',
                borderRightColor: 'transparent'
              }}
            >
              <div className="form-check">
                <input
                  className="form-check-input position-static"
                  onClick={this.checkboxHandler}
                  type="checkbox"
                  id="blankCheckbox"
                  value={item.number}
                  aria-label="..."
                />
              </div>
            </td>
          </tr>
        );
      } else return null;
    });

    return (
      <div style={{ marginLeft: '20%', marginRight: '20%' }}>
        <table className="table table-bordered table-responsive table-striped ">
          <thead className="thead-dark">
            <tr>
              <th
                className="text-center align-middle"
                style={{ border: '1px solid #ccc' }}
                colSpan="2"
              >
                Name
              </th>
              <th
                className="text-center align-middle"
                style={{ border: '1px solid #ccc' }}
                colSpan="2"
              >
                Public Number
              </th>
              <th
                className="text-center align-middle"
                style={{ border: '1px solid #ccc' }}
                colSpan="3"
              >
                Short Number
              </th>
              <th
                className="text-center align-middle"
                style={{ border: '1px solid #ccc' }}
              >
                Alarm Call
              </th>
            </tr>
          </thead>
          <tbody>
            {cells}
            <tr>
              <td
                className="text-center align-middle"
                style={{ width: '10%', border: '1px solid #ccc' }}
                colSpan="2"
              >
                Call Conference
              </td>
              <td
                className="text-center align-middle"
                style={{
                  width: '30%',
                  border: '1px solid #ccc',
                  borderRightColor: 'transparent'
                }}
              >
                034439431
              </td>
              <td
                className="text-center align-middle"
                style={{ width: '10%', border: '1px solid #ccc' }}
              >
                <button
                  style={{ display: 'none' }}
                  className="btn bg-transparent"
                >
                  <i className="fa fa-trash" aria-hidden="true"></i>
                </button>
              </td>
              <td
                className="text-center align-middle"
                style={{
                  width: '20%',
                  border: '1px solid #ccc',
                  borderRightColor: 'transparent'
                }}
              >
                99
              </td>
              <td
                className="text-center align-middle"
                style={{
                  width: '10%',
                  border: '1px solid #ccc',
                  borderRightColor: 'transparent'
                }}
              >
                <button style={{ display: 'none' }} className="ui icon button">
                  <i aria-hidden="true" className="trash alternate icon"></i>
                </button>
              </td>
              <td
                className="text-center align-middle"
                style={{ width: '10%', border: '1px solid #ccc' }}
              >
                <button
                  style={{ display: 'none' }}
                  className="btn bg-transparent"
                >
                  <i className="fa fa-trash" aria-hidden="true"></i>
                </button>
              </td>
              <td
                className="text-center align-middle"
                style={{ width: '10%', border: '1px solid #ccc' }}
              ></td>
            </tr>
          </tbody>
        </table>

        <Modal
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.openShortNumber}
          onHide={this.closeShortNumber}
        >
          <Modal.Header closeButton>
            <Modal.Title>Delete Your Number</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>Are you sure you want to delete this number</p>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={this.closeShortNumber} variant="secondary">
              No
            </Button>
            <Button onClick={this.deleteShortNumberHandler} variant="primary">
              Yes
            </Button>
          </Modal.Footer>
        </Modal>

        <Modal
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.openPublicNumber}
          onHide={this.closePublicNumber}
        >
          <Modal.Header closeButton>
            <Modal.Title>Delete Your Number</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>Are you sure you want to delete this number</p>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={this.closePublicNumber} variant="secondary">
              No
            </Button>
            <Button onClick={this.deletePublicNumberHandler} variant="primary">
              Yes
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
}

export default PhoneList;
