import React from 'react';
import PhoneList from './PhoneList';
import AddPublicNumber from './AddPublicNumber';
import Demo from './Demo';

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {shortNumbers: [], showNumbers: false, updateStatus: ''};
	}

	componentDidMount() {
		fetch("http://localhost/post.php")
		    .then(response => {
				return response.json();
			}).then(result => {
				this.setState({
					shortNumbers:result
				});
			});
	}

	togglePhoneNumber = () => {
		this.setState({showNumbers: !this.state.showNumbers})
	}

	render() {                            
		return (
			<div style={{marginTop: '50px'}}>
			<i class="ellipsis vertical icon"></i>
	    		<div className="ui center aligned grid" >
					<div className="four wide column">ID</div>
					<div className="four wide column">Public Number</div>
					<div className="four wide column">Short Number</div>
					<div className="four wide column"></div>
				</div>
				<div><PhoneList shortNumbers={this.state.shortNumbers} /></div>
				<div className="ui center aligned grid"> 
					<button onClick ={this.togglePhoneNumber} className="positive ui button">Add Number</button>
				</div>
				{this.state.showNumbers ? <div><AddPublicNumber /></div> : null}			
			</div>
		)
	}
}
export default App;

