import React from 'react';
import { Table } from 'semantic-ui-react';
import './PhoneList.css';
import { Button, Modal } from 'semantic-ui-react';
import EditNameModal from './EditNameModal';
import EditShortNumberModal from './EditShortNumberModal';

class PhoneList extends React.Component {

	state = { openShortNumber: false, openPublicNumber: false, id: '' }

	openShortNumber = () => this.setState({ openShortNumber: true });
	closeShortNumber = () => this.setState({ openShortNumber: false });

	openPublicNumber = () => this.setState({ openPublicNumber: true });
	closePublicNumber = () => this.setState({ openPublicNumber: false });

	deleteShortNumberModal = (e) => {
		this.openShortNumber();
		this.setState({ id: e.target.id });
	}

	deletePublicNumberModal = (e) => {
		this.openPublicNumber();
		this.setState({ id: e.target.id });
	}

	deleteShortNumberHandler = () => {
		this.props.onSubmitSN(this.state.id);
		this.closeShortNumber();
	}

	deletePublicNumberHandler = (e) => {
		this.props.onSubmitPN(this.state.id);
		this.closePublicNumber();
	}

	render() {

		const { openShortNumber } = this.state;
		const { openPublicNumber } = this.state;

		const cells = this.props.data.map((item, index) => {
			if (item.number !== "35834439430" && item.number !== "35834439431") {
				return (
					<Table.Row key={index}>
						<Table.Cell textAlign='center' style={{ width: '10%', border: '2px solid #ccc', borderColor: 'black', borderRightColor: 'transparent' }}>{item.info}</Table.Cell>
						<Table.Cell textAlign='center' style={{ width: '10%', border: '2px solid #ccc', borderColor: 'black', }}><EditNameModal id={item.id} /></Table.Cell>
						<Table.Cell textAlign='center' style={{ width: '30%', border: '2px solid #ccc', borderRightColor: 'transparent' }}>{item.number}</Table.Cell>
						<Table.Cell textAlign='center' style={{ width: '10%', border: '2px solid #ccc', borderColor: 'black', }}><button id={item.id} onClick={this.deletePublicNumberModal} className="ui icon button"><i aria-hidden="true" className="trash alternate icon"></i></button></Table.Cell>
						<Table.Cell textAlign='center' style={{ width: '5%' , border: '2px solid #ccc', borderColor: 'black', borderRightColor: 'transparent' }}>{item.short_number}</Table.Cell>
						<Table.Cell textAlign='center' style={{ width: '10%', border: '2px solid #ccc', borderColor: 'black', borderRightColor: 'transparent' }}>{item.short_number !== undefined ? <EditShortNumberModal short_number={item.short_number} id={item.id} /> : null}</Table.Cell>
						<Table.Cell textAlign='center' style={{ width: '10%', border: '2px solid #ccc' }}>{item.short_number !== undefined ? <button id={item.short_number} onClick={this.deleteShortNumberModal} className="ui icon button"><i aria-hidden="true" className="trash alternate icon"></i></button> : null}</Table.Cell>
					</Table.Row>
				);
			}
			else return null;
		});
		//celled
		//collapsing
		return (
			<div style={{ marginLeft: '20%', marginRight: '20%' }}>
				<Table celled structured>
					<Table.Header>
						<Table.Row>
							<Table.HeaderCell textAlign='center' style={{ border: '2px solid #ccc' }} colSpan='2'>Name</Table.HeaderCell>
							<Table.HeaderCell textAlign='center' style={{ border: '2px solid #ccc' }} colSpan='2'>Public Number</Table.HeaderCell>
							<Table.HeaderCell textAlign='center' style={{ border: '2px solid #ccc' }} colSpan='3'>Short Number</Table.HeaderCell>
						</Table.Row>
					</Table.Header>
					<Table.Body>
						{cells}
						<Table.Row>
							<Table.Cell textAlign='center' style={{ width: '10%', border: '2px solid #ccc' }} colSpan='2'>Call Conference</Table.Cell>
							<Table.Cell textAlign='center' style={{ width: '30%', border: '2px solid #ccc', borderRightColor: 'transparent' }}>034439431</Table.Cell>
							<Table.Cell textAlign='center' style={{ width: '10%', border: '2px solid #ccc' }}><button style={{display: 'none'}} className="ui icon button"><i aria-hidden="true" className="trash alternate icon"></i></button></Table.Cell>
							<Table.Cell textAlign='center' style={{ width: '20%', border: '2px solid #ccc', borderRightColor: 'transparent' }}>99</Table.Cell>
							<Table.Cell textAlign='center' style={{ width: '10%', border: '2px solid #ccc', borderRightColor: 'transparent' }}><button style={{display: 'none'}} className="ui icon button"><i aria-hidden="true" className="trash alternate icon"></i></button></Table.Cell>
							<Table.Cell textAlign='center' style={{ width: '10%', border: '2px solid #ccc' }}><button style={{display: 'none'}} className="ui icon button"><i aria-hidden="true" className="trash alternate icon"></i></button></Table.Cell>
						</Table.Row>
					</Table.Body>
				</Table>
				<Modal
					open={openShortNumber}
					onOpen={this.openShortNumber}
					onClose={this.closeShortNumber}
				>
					<Modal.Header>Delete Your Number</Modal.Header>
					<Modal.Content>
						<p>Are you sure you want to delete this number</p>
					</Modal.Content>
					<Modal.Actions>
						<Button onClick={this.closeShortNumber} negative>
							No
            			</Button>
						<Button
							onClick={this.deleteShortNumberHandler}
							positive
							labelPosition='right'
							icon='checkmark'
							content='Yes'
						/>
					</Modal.Actions>
				</Modal>
				<Modal
					open={openPublicNumber}
					onOpen={this.openPublicNumber}
					onClose={this.closePublicNumber}
				>
					<Modal.Header>Delete Your Number</Modal.Header>
					<Modal.Content>
						<p>Are you sure you want to delete this number</p>
					</Modal.Content>
					<Modal.Actions>
						<Button onClick={this.closePublicNumber} negative>
							No
            			</Button>
						<Button
							onClick={this.deletePublicNumberHandler}
							positive
							labelPosition='right'
							icon='checkmark'
							content='Yes'
						/>
					</Modal.Actions>
				</Modal>
			</div>
		);
	}
};

export default PhoneList;
