import React from 'react';
import TextInputGroup from "./TextInputGroup";

class EditName extends React.Component {

	state = {
		value: 'select',
		id: '',
		name: '',
		error: null,
		errors: {
			value: '',
			name: ""
		}
	};

	onChangeValue = e => {

		if (this.state.errors.value !== "") {
			this.setState({
				errors: {
					value: ""
				}
			});
		}

		this.setState({ value: e.target.value, id: e.target.options[e.target.selectedIndex].getAttribute('id') });
	};

	onChangeName = e => {

		if (this.state.errors.name !== "") {
			this.setState({
				errors: {
					name: ""
				}
			});
		}
		this.setState({ name: e.target.value });
	};

	postDataHandler = (e) => {
		const value = this.state.value;
		const name = this.state.name;

		if (value === "select") {
			e.preventDefault();
			this.setState({
				errors: { value: "Please select a number" }
			});
			return;
		}

		if (name === "") {
			e.preventDefault();
			this.setState({
				errors: { name: "Name is required" }
			});
			return;
		}

		console.log(this.state.id);
		console.log(this.state.name);
		fetch('http://localhost/editNumber.php', {
			method: 'POST',
			headers: {
				'Accept': 'application/json',
			},
			body: JSON.stringify({
				id: this.state.id,
				name: this.state.name
			})
		}).catch(error => this.setState({ error }));
	}

	render() {

		const
			modifedData = [...this.props.data],
			keys = ['number'],
			filtered = modifedData.filter(
				(s => o =>
					(k => !s.has(k) && s.add(k))
						(keys.map(k => o[k]).join('|'))
				)
					(new Set)
			);

		return (
			<div>
				<div className="ui one column stackable center aligned page grid">
					<div className="column twelve wide"></div>
				</div>
				<hr />
				<div className="ui centered card" style={{ padding: "30px" }}>
					<form className="ui form">
						<div style={{ marginBottom: '10px' }}><strong>Public number</strong></div>
						<div style={{ marginBottom: '10px' }} >
							<select 
								onChange={this.onChangeValue}>
								<option value="select">Select a number</option>
								{filtered.map((number) => <option key={number.id} id={number.id} value={number.number}>{number.number}</option>)}
							</select>
							{this.state.errors.value ? (
								<div className="ui basic red pointing prompt label transition visible">
									{this.state.errors.value}
								</div>
							) : null}
						</div>

						<TextInputGroup
							type="text"
							value={this.state.name}
							placeholder="Enter a name"
							onChange={this.onChangeName}
							name="name"
							label="Name"
							error={this.state.errors.name}
						/>
						<button
							onClick={this.postDataHandler}
							className="ui blue button"
							style={{ marginTop: "10px" }} >
							Submit
						</button>
					</form>
				</div>
			</div>
		);
	}
}

export default EditName;
