import React from 'react';
import PhoneList from './PhoneList'

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {shortNumbers: []};
	}

	showNumber(){
		fetch("http://localhost/getShortNumber.php")
		.then(response => {
			return response.json();
		}).then(result => {
			this.setState({
				shortNumbers:result
			});
		});
	}

	componentDidMount() {

	}

	render() {                            
		return (
			<div>
			<div className="ui center aligned grid">
			<div className="four wide column">ID</div>
			<div className="four wide column">Public Number</div>
			<div className="four wide column">Short Number</div>
			<div className="four wide column"></div>
			</div>
			<div><PhoneList shortNumbers={this.state.shortNumbers} /></div>
			<div className="ui center aligned grid">
			<button className="positive ui button ">Add Number</button>
			</div>
			</div>
			)
	}
}
export default App;
