import React from 'react';
import PhoneList from './PhoneList';
import AddNumberModal from './AddNumberModal';
import { Menu } from 'semantic-ui-react';
import TableForPrint from './TableForPrint';
import XLSX from 'xlsx';

class App extends React.Component {

	state = { activeItem: 'home', data: [], error: null, fileName: 'contact.xlsx' };

	customers = () => {
		let customers = this.state.data.map(({
			alert_info_enabled, aservicemask_id, forward_autoself,
			number, language_id, notify, operator_id, number_id, id,
			type, use_default, voiplocation_code, company_id, whitelist,
			whitelist_mode, whitelist_pin_in, whitelist_pin_in_enabled,
			whitelist_pin_out, whitelist_pin_out_enabled, ...item }) => item);
		customers = customers.map(({info, short_number}) => ({ Name: info, Number: short_number}));
		customers = customers.filter(person => person.Name !== 'Opas test IVR number');
		customers = customers.filter(person => person.Name !== 'Call Conference');
		customers.push({"Name": "", "Number": ""});
		customers.push({"Name": "Call Conference", "Number": "99"});
		customers.push({"Name": "", "Number": ""});
		customers.push({"Name": "Palvelu Numero", "Number": "034439430"});
		return customers;
	}

	printData = () => {
		const divToPrint = document.getElementById("printTable");
		const newWin = window.open("");
		newWin.document.write(divToPrint.outerHTML);
		newWin.print();
		newWin.close();
	}


	exportFile = () => {
		/* convert state.data to worksheet */
		const ws = XLSX.utils.json_to_sheet(this.customers());
		/* build a workbook */
		const wb = XLSX.utils.book_new();
		XLSX.utils.book_append_sheet(wb, ws, "SheetJS");
		/* generate XLSX file and send to client */
		XLSX.writeFile(wb, "contact.xlsx")
	}

	loadData() {
		fetch("http://localhost/data.php")
			.then(response => {
				return response.json();
			}).then(result => {
				this.setState({
					data: result
				});
			}).catch(error => this.setState({ error }));
	}

	componentDidMount() {
		this.loadData();
	}

	deleteShortNumberHandler = (id) => {
		fetch('http://localhost/removeShortNumber.php', {
			method: 'POST',
			headers: {
				'Accept': 'application/json',
			},
			body: JSON.stringify({
				short_number: id
			})
		})
			.then(() => {
				this.loadData();
			}).catch(error => this.setState({ error }));
	}

	deletePublicNumberHandler = (id) => {
		fetch('http://localhost/removePublicNumber.php', {
			method: 'POST',
			headers: {
				'Accept': 'application/json',
			},
			body: JSON.stringify({
				number_id: id
			})
		})
			.then(() => {
				this.loadData();
			}).catch(error => this.setState({ error }));
	}

	handleItemClick = (e, { name }) => this.setState({ activeItem: name })

	render() {
		const { activeItem } = this.state
		return (
			<div style={{paddingBottom: '100px'}}>
				<Menu stackable size='small' inverted>
					<Menu.Item >
						<img alt="Logo" className="ui tiny image" src='http://www.nsftele.com/sites/default/files/logo_2_vari.png' />
					</Menu.Item>
					<Menu.Item name='home' active={activeItem === 'home'} onClick={this.handleItemClick} />
					<Menu.Item name='service areas' active={activeItem === 'service areas'} onClick={this.handleItemClick} />
					<Menu.Item name='customers' active={activeItem === 'customers'} onClick={this.handleItemClick} />
					<Menu.Item name='company' active={activeItem === 'company'} onClick={this.handleItemClick} />
					<Menu.Item name='contact us' active={activeItem === 'contact us'} onClick={this.handleItemClick} />
				</Menu>
				{this.state.error ?
					<div><p>Network error: {this.state.error.message}</p></div> :
					(<div>
						<div style={{paddingLeft: '20px', paddingTop: '20px', display: 'inline-block'}} className="col-md-4 center">
							<button className="ui button" onClick={this.exportFile}>Export To Excel</button>
							<button className="ui button" onClick={this.printData}>Print Table</button>
						</div>
						<div style={{ fontSize: 'large', paddingLeft: '20px'}}><br/>Palvelu Numero: 034439430</div>
						<div style={{ marginTop: '30px', marginBottom: '30px' }} className="ui center aligned grid">
							<AddNumberModal data={this.state.data} />
						</div>
						<div><PhoneList onSubmitPN={this.deletePublicNumberHandler} onSubmitSN={this.deleteShortNumberHandler} data={this.state.data} /></div>
						<div><TableForPrint data={this.state.data} /></div>
					</div>)
				}
			</div>
		)
	}
}
export default App;
