	*/

	deleteShortNumberHandler = (e) => {
		console.log('clickeda!');
	}

	deletePublicNumberHandler = (e) => {
		e.preventDefault();
		console.log(e.target.id);
		this.props.onSubmitPN(e.target.id)
	}

	render() {

		const cells = this.props.data.map(item => {
			return (
				<Table.Row key={item.id}>
					<Table.Cell textAlign='center' style={{width: '10%', border: '1px solid #ccc'}}>{item.id}</Table.Cell>
					<Table.Cell textAlign='center' style={{width: '30%', border: '1px solid #ccc', borderRightColor: 'transparent'}}>{item.number}</Table.Cell>
					<Table.Cell textAlign='center' style={{width: '10%', border: '1px solid #ccc'}}><button id={item.id} onClick={this.deletePublicNumberHandler} className="ui icon button"><i aria-hidden="true" className="trash alternate icon"></i></button></Table.Cell>
					<Table.Cell textAlign='center' style={{width: '20%', border: '1px solid #ccc', borderRightColor: 'transparent'}}>{item.short_number}</Table.Cell>
					<Table.Cell textAlign='center' style={{width: '10%', border: '1px solid #ccc'}}>{item.short_number !== undefined ? <button id={item.short_number} onClick={this.deleteShortNumberHandler} className="ui icon button"><i aria-hidden="true" className="trash alternate icon"></i></button> : null}</Table.Cell>
				</Table.Row>
			);
		});
		//celled
		//collapsing
		return (
			<div style={{ marginLeft: '20%', marginRight: '20%' }}>
				<Table celled structured>
					<Table.Header>
						<Table.Row>
							<Table.HeaderCell textAlign='center' style={{border: '1px solid #ccc'}}>Id</Table.HeaderCell>
							<Table.HeaderCell textAlign='center' style={{border: '1px solid #ccc'}} colSpan='2'>Public Number</Table.HeaderCell>
							<Table.HeaderCell textAlign='center' style={{border: '1px solid #ccc'}} colSpan='2'>Short Number</Table.HeaderCell>
						</Table.Row>
					</Table.Header>
					<Table.Body>
						{cells}
					</Table.Body>
				</Table>
			</div>
		);
	}
};

export default PhoneList;

