import React from 'react';
import TextInputGroup from "./TextInputGroup";

class AddOrUpdateShortNumber extends React.Component {

	state = {
		value: '',
		id: '',
		phone_no: '',
		errors: {
			phone_no: ""
		}
	};

	postDataHandler = () => {
		console.log(this.state.id);
		fetch('http://localhost/addOrUpdateShortNumber.php', {
			method: 'POST',
			headers: {
				'Accept': 'application/json',
			},
			body: JSON.stringify({
				id: 1086,
				short_number: '08'
			})
		})
	}

	render() {
		return (
			<div>
				<div className="ui one column stackable center aligned page grid">
					<div className="column twelve wide"></div>
				</div>
				<hr />
				<div className="ui centered card" style={{ padding: "30px" }}>
					<form className="ui form">
						<div style={{ marginBottom: '10px' }}><strong>Choose your public number</strong></div>
						<div style={{ marginBottom: '10px' }} >
								<select value={this.state.value} 
									onChange={(e) => console.log(e.target.options[e.target.selectedIndex].key)}>
									<option value="select">Select a number</option>
									{this.props.shortNumbers.map((number) => <option key={number.id} value={number.number}>{number.number}</option>)}
								</select>
						</div>
						<TextInputGroup
							type="text"
							value={this.state.phone_no}
							placeholder="Enter Your Phone Number"
							onChange={(event) => this.setState({ phone_no: event.target.value })}
							name="phone_no"
							label="Phone Number"
							error={this.state.errors.phone_no}
						/>
						<button
							onClick={this.postDataHandler}
							className="ui blue button"
							style={{ marginTop: "10px" }} >
							Submit {console.log(this.state.id)}
						</button>
					</form>
				</div>
			</div>
		);
	}
}
export default AddOrUpdateShortNumber;
