import React from 'react';

class TestAddPublicNumber extends React.Component {

	handler = () => {
		fetch('http://localhost/addPublicNumber.php', {
		  method: 'POST', 
		  headers: {	  	
		    'Accept': 'application/json',
			//'Content-Type': 'application/json', //dont use this, will cause prefligt
			'Access-Control-Max-Age': 600
		  },
		  body: JSON.stringify({
		    public_number: '29'
		  })
		})
	}

	render() {                            
		return (
			<div>
				<button onClick={this.handler}>Click Me</button>
			</div>
		)
	}
}
export default TestAddPublicNumber;
