import React, { Component } from 'react';
import AddReferenceCall from '../../APICalls/AddReferenceCall';
import AddOrUpdateShortNumber from '../../APICalls/AddOrUpdateShortNumber';
import { Button, Modal } from 'react-bootstrap';
import { connect } from 'react-redux';
import axios from 'axios';

class COOBModal extends Component {
  state = {
    showModal: false,
    showMakeModal: false,
    showDeleteModal: false,
    showDetailModal: false,
    showLogModal: false,
    showConferenceErrorModal: false
  };

  handleShowModal = () => this.setState({ showModal: true });
  handleCloseModal = () => this.setState({ showModal: false });

  handleShowMakeModal = () => {
    if (this.props.conferenceNumbers.length === 0) {
      this.setState({
        showModal: false,
        showMakeModal: false,
        showDeleteModal: false,
        showDetailModal: false,
        showLogModal: false,
        showConferenceErrorModal: true
      });
    } else {
      this.setState({
        showMakeModal: true,
        showDeleteModal: false,
        showDetailModal: false,
        showLogModal: false,
        showConferenceErrorModal: false
      });
    }
  };
  handleCloseMakeModal = () =>
    this.setState({
      showMakeModal: false,
      showDeleteModal: false,
      showDetailModal: false,
      showLogModal: false,
      showConferenceErrorModal: false
    });

  handleShowDeleteModal = () => {
    if (this.props.conferenceNumbers.length === 0) {
      this.setState({
        showModal: false,
        showMakeModal: false,
        showDeleteModal: false,
        showDetailModal: false,
        showLogModal: false,
        showConferenceErrorModal: true
      });
    } else {
      this.setState({
        showMakeModal: false,
        showDeleteModal: true,
        showDetailModal: false,
        showLogModal: false,
        showConferenceErrorModal: false
      });
    }
  };

  handleCloseDeleteModal = () =>
    this.setState({
      showMakeModal: false,
      showDeleteModal: false,
      showDetailModal: false,
      showLogModal: false,
      showConferenceErrorModal: false
    });

  handleShowDetailModal = () => {
    if (this.props.conferenceNumbers.length === 0) {
      this.setState({
        showModal: false,
        showMakeModal: false,
        showDeleteModal: false,
        showDetailModal: false,
        showLogModal: false,
        showConferenceErrorModal: true
      });
    } else {
      this.setState({
        showMakeModal: false,
        showDeleteModal: false,
        showDetailModal: true,
        showLogModal: false,
        showConferenceErrorModal: false
      });
    }
  };

  handleCloseDetailModal = () =>
    this.setState({
      showMakeModal: false,
      showDeleteModal: false,
      showDetailModal: false,
      showLogModal: false,
      showConferenceErrorModal: false
    });

  handleShowLogModal = () =>
    this.setState({
      showMakeModal: false,
      showDeleteModal: false,
      showDetailModal: false,
      showLogModal: true,
      showConferenceErrorModal: false
    });
  handleCloseLogModal = () =>
    this.setState({
      showMakeModal: false,
      showDeleteModal: false,
      showDetailModal: false,
      showLogModal: false,
      showConferenceErrorModal: false
    });

  handleShowConferenceErrorModal = () =>
    this.setState({
      showMakeModal: false,
      showDeleteModal: false,
      showDetailModal: false,
      showLogModal: true,
      showConferenceErrorModal: true
    });
  handleCloseConferenceErrorModal = () =>
    this.setState({
      showMakeModal: false,
      showDeleteModal: false,
      showDetailModal: false,
      showLogModal: false,
      showConferenceErrorModal: false
    });

  deleteConferenceNumber = () =>
    this.props.conferenceNumbers.forEach(number => {
      axios
        .post(
          'https://demo2.nsftele.com/travel-guide-php/deleteConferenceCall.php',
          {
            a_number: number
          }
        )
        .then(
          res => {
            console.log(res);
            console.log(res.data);
            //window.location.reload(true);
          },
          error => {
            console.log(error);
          }
        );
    });

  componentDidMount() {
    const { childRef } = this.props;
    childRef(this);
  }
  componentWillUnmount() {
    const { childRef } = this.props;
    childRef(undefined);
  }

  render() {
    return (
      <>
        <Modal
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.showModal}
          onHide={this.handleCloseModal}
        >
          <Modal.Header closeButton>
            <Modal.Title>Step #1</Modal.Title>
          </Modal.Header>
          <Modal.Body>Please choose an option</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.handleShowMakeModal}>
              Add Call
            </Button>
            <Button variant="secondary" onClick={this.handleShowDetailModal}>
              Show Call
            </Button>
            <Button variant="secondary" onClick={this.handleShowDeleteModal}>
              Delele Call
            </Button>
            <Button variant="secondary" onClick={this.handleShowLogModal}>
              Call Log
            </Button>
          </Modal.Footer>
        </Modal>

        <Modal
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.showMakeModal}
          onHide={this.handleCloseMakeModal}
        >
          <Modal.Header closeButton>
            <Modal.Title>Step #2</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <div>
              <AddReferenceCall />
            </div>
          </Modal.Body>
        </Modal>

        <Modal
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.showDeleteModal}
          onHide={this.handleCloseDeleteModal}
        >
          <Modal.Header closeButton>
            <Modal.Title>Delete Scheduled Call</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>Are you sure you want to delete this scheduled call?</p>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={this.props.close} variant="secondary">
              No
            </Button>
            <Button onClick={this.deleteConferenceNumber} variant="primary">
              Yes
            </Button>
          </Modal.Footer>
        </Modal>

        <Modal
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.showDetailModal}
          onHide={this.handleCloseDetailModal}
        >
          <Modal.Header closeButton>
            <Modal.Title>Step #2</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <div>
              <AddOrUpdateShortNumber data={this.props.data} />
            </div>
          </Modal.Body>
        </Modal>

        <Modal
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.showLogModal}
          onHide={this.handleCloseLogModal}
        >
          <Modal.Header closeButton>
            <Modal.Title>Step #2</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <div>
              <AddOrUpdateShortNumber data={this.props.data} />
            </div>
          </Modal.Body>
        </Modal>

        <Modal
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.showConferenceErrorModal}
          onHide={this.handleCloseConferenceErrorModal}
        >
          <Modal.Header closeButton>
            <Modal.Title>Error!</Modal.Title>
          </Modal.Header>
          <Modal.Body>Please choose at least a conference number</Modal.Body>
        </Modal>
      </>
    );
  }
}

const mapStateToProps = state => {
  return {
    conferenceNumbers: state.conference.conferenceNumbers
  };
};

export default connect(mapStateToProps)(COOBModal);
