import React from 'react';

class AddReferenceCall extends React.Component {
  state = {
    first_time: '',
    a_number: '',
    date: '',
    time: '',
    error: null,
    errors: {
      a_number: '',
      date: '',
      time: ''
    }
  };

  validateDate = inputText => {
    //const dateformat = /[0-9]{4}-(0[1-9]|1[0-2])-(0[1-9]|[1-2][0-9]|3[0-1])/;
    //const dateformat = /(2[0-3]|[01][0-9]):[0-5][0-9]/;

    const dateformat = /[0-9]{4}-(0[1-9]|1[0-2])-(0[1-9]|[1-2][0-9]|3[0-1])/;
    // Match the date format through regular expression
    if (inputText.value.match(dateformat)) {
      const pdate = inputText.value.split('-');
      const dd = parseInt(pdate[0]);
      const mm = parseInt(pdate[1]);
      const yy = parseInt(pdate[2]);
      // Create list of days of a month [assume there is no leap year by default]
      const ListofDays = [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];
      if (mm == 1 || mm > 2) {
        if (dd > ListofDays[mm - 1]) {
          return false;
        }
      }
      if (mm == 2) {
        let lyear = false;
        if ((!(yy % 4) && yy % 100) || !(yy % 400)) {
          lyear = true;
        }
        if (lyear == false && dd >= 29) {
          return false;
        }
        if (lyear == true && dd > 29) {
          return false;
        }
      }
    } else {
      return false;
    }
  };

  onChange = e => {
    if (
      this.state.errors.a_number !== '' ||
      this.state.errors.date !== '' ||
      this.state.errors.time !== ''
    ) {
      this.setState({
        errors: {
          a_number: '',
          date: '',
          time: ''
        }
      });
    }
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  postDataHandler = e => {
    if (
      this.state.a_number === '' &&
      this.state.date === '' &&
      this.state.time === ''
    ) {
      e.preventDefault();
      this.setState({
        errors: {
          a_number: 'Phone number is required',
          date: 'Date is required',
          time: 'Time number is required'
        }
      });
      return;
    }

    //if (!this.state.public_number.match(/^\d{12}$/) && !this.state.public_number.match(/^\d{13}$/)) {
    if (!/^[3][5][8][0-9]/.test(this.state.a_number)) {
      e.preventDefault();
      this.setState({
        errors: { a_number: 'Invalid, must start with 358' }
      });
      return;
    }

    if (this.state.date === '') {
      e.preventDefault();
      this.setState({
        errors: { date: 'Date is required' }
      });
      return;
    }

    if (this.state.time === '') {
      e.preventDefault();
      this.setState({
        errors: { time: 'Time number is required' }
      });
      return;
    }

    if (
      this.state.errors.a_number === '' &&
      this.state.errors.date === '' &&
      this.state.errors.time === ''
    ) {
      fetch('http://demo2.nsftele.com/travel-guide-php/addPublicNumber.php', {
        method: 'POST',
        headers: {
          Accept: 'application/json'
        },
        body: JSON.stringify({
          name: this.state.name,
          public_number: this.state.public_number
        })
      }).catch(error => this.setState({ error }));
    } else {
      console.log(this.state.errors);
    }
  };

  render() {
    return (
      <div className="card">
        <div className="card-body">
          <form>
            <div className="form-group">
              <label htmlFor="Name">Name</label>
              <input
                onChange={this.onChange}
                type="text"
                value={this.state.name}
                className="form-control"
                placeholder="Enter Your Name"
                name="name"
              />
              {this.state.errors.name ? (
                <small className="form-text text-danger">
                  {this.state.errors.name}
                </small>
              ) : null}
            </div>
            <div className="form-group">
              <label htmlFor="public_number">Phone Number</label>
              <input
                onChange={this.onChange}
                type="text"
                value={this.state.public_number}
                className="form-control"
                placeholder="Enter Your Phone Number"
                name="public_number"
              />
              {this.state.errors.public_number ? (
                <small className="form-text text-danger">
                  {this.state.errors.public_number}
                </small>
              ) : null}
            </div>
            <button
              onClick={this.postDataHandler}
              type="submit"
              className="btn btn-primary"
            >
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default AddReferenceCall;
