import React from "react";
import TextInputGroup from "./TextInputGroup";

class EditShortNumber extends React.Component {
  state = {
    id: "",
    short_number: "",
    error: null,
    errors: {
      short_number: ""
    }
  };

  onChangeShortNumber = e => {
    if (this.state.errors.short_number !== "") {
      this.setState({
        errors: {
          short_number: ""
        }
      });
    }
    this.setState({ short_number: e.target.value });
  };

  postDataHandler = e => {
    const short_number = this.state.short_number;

    if (short_number === "") {
      e.preventDefault();
      this.setState({
        errors: { short_number: "Short number is required" }
      });
      return;
    }

    if (short_number === "99") {
      e.preventDefault();
      this.setState({
        errors: { short_number: "99 is reserved for conference number" }
      });
      return;
    }

    if (!short_number.match(/^\d{2}$/)) {
      e.preventDefault();
      this.setState({
        errors: { short_number: "Must have two digits" }
      });
      return;
    }
    if (short_number === "") {
      e.preventDefault();
      this.setState({
        errors: { short_number: "short_number is required" }
      });
      return;
    }

    fetch("http://demo2.nsftele.com/travel-guide-php/removeShortNumber.php", {
      method: "POST",
      headers: {
        Accept: "application/json"
      },
      body: JSON.stringify({
        short_number: this.props.short_number
      })
    }).catch(error => this.setState({ error }));

    fetch(
      "http://demo2.nsftele.com/travel-guide-php/addOrUpdateShortNumber.php",
      {
        method: "POST",
        headers: {
          Accept: "application/json"
        },
        body: JSON.stringify({
          id: this.props.id,
          short_number: this.state.short_number
        })
      }
    ).catch(error => this.setState({ error }));
  };

  render() {
    return (
      <div>
        <div className="ui one column stackable center aligned page grid">
          <div className="column twelve wide" />
        </div>
        <hr />
        <div className="ui centered card" style={{ padding: "30px" }}>
          <form className="ui form">
            <TextInputGroup
              type="text"
              value={this.state.name}
              placeholder="Enter a new short number"
              onChange={this.onChangeShortNumber}
              name="short_number"
              label="New Short Number"
              error={this.state.errors.short_number}
            />
            <button
              onClick={this.postDataHandler}
              className="ui blue button"
              style={{ marginTop: "10px" }}
            >
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default EditShortNumber;
