import React from 'react';
import PhoneList from './PhoneList'
require('request').defaults({ rejectUnauthorized: false });

class RestController extends React.Component {
	constructor(props) {
		super(props);
		this.state = {numbers: []};
	}

	componentDidMount() {
		fetch("http://localhost/getShortNumber.php")
		    .then(response => {
				return response.json();
			}).then(result => {
				this.setState({
					numbers:result
				});
			});
	}

	render() {                            
		return (
			<div>
				<div><PhoneList numbers={this.state.numbers} /></div>
			</div>
		)
	}
}
export default RestController;
