import React from 'react';
import TextInputGroup from "./TextInputGroup";

class AddOrUpdateShortNumber extends React.Component {

	state = {
		value: 'select',
		id: '',
		short_number: '',
		error: null,
		errors: {
			value: '',
			short_number: ""
		}
	};

	onChangeValue = e => {

		if (this.state.errors.value !== "") {
			this.setState({
				errors: {
					value: ""
				}
			});
		}

		this.setState({ value: e.target.value, id: e.target.options[e.target.selectedIndex].getAttribute('id') });
	};

	onChangeShortNumber = e => {

		if (this.state.errors.short_number !== "") {
			this.setState({
				errors: {
					short_number: ""
				}
			});
		}
		this.setState({ short_number: e.target.value });
	};

	postDataHandler = (e) => {
		const value = this.state.value;
		const short_number = this.state.short_number;

		if (value === "select") {
			e.preventDefault();
			this.setState({
				errors: { value: "Please select a number" }
			});
			return;
		}

		if (short_number === "") {
			e.preventDefault();
			this.setState({
				errors: { short_number: "Short number is required" }
			});
			return;
		}

		if (!(short_number.match( /^\d{2}$/))) {
			e.preventDefault();
			this.setState({
				errors: { short_number: "Must have two digits" }
			});
			return;
		}

		fetch('http://localhost/addOrUpdateShortNumber.php', {
			method: 'POST',
			headers: {
				'Accept': 'application/json',
			},
			body: JSON.stringify({
				id: this.state.id,
				short_number: this.state.short_number
			})
		}).catch(error => this.setState({ error }));
	}

	render() {

		const modifedData = [...this.props.data];
		const filtered = Object.values(
			modifedData.reduce((a, c) => {
				a[c.number] = c;
				return a
			}, {}));
		const numbers = filtered.map((number) => {
			if (number.number !== "35834439430" && number.number !== "034541541" && number.number !== "35834541541") {
				return (
					<option key={number.id} id={number.id} value={number.number}>{number.info}</option>
				)
			}
			else return null;
		});

		return (
			<div>
				<div className="ui one column stackable center aligned page grid">
					<div className="column twelve wide"></div>
				</div>
				<hr />
				<div className="ui centered card" style={{ padding: "30px" }}>
					<form className="ui form">
						<div style={{ marginBottom: '10px' }}><strong>Name</strong></div>
						<div style={{ marginBottom: '10px' }} >
							<select 
								onChange={this.onChangeValue}>
								<option value="select">Select a name</option>
								{numbers}
							</select>
							{this.state.errors.value ? (
								<div className="ui basic red pointing prompt label transition visible">
									{this.state.errors.value}
								</div>
							) : null}
						</div>

						<TextInputGroup
							type="text"
							value={this.state.short_number}
							placeholder="Enter a short number"
							onChange={this.onChangeShortNumber}
							name="short_number"
							label="Short Number"
							error={this.state.errors.short_number}
						/>
						<button
							onClick={this.postDataHandler}
							className="ui blue button"
							style={{ marginTop: "10px" }} >
							Submit
						</button>
					</form>
				</div>
			</div>
		);
	}
}
export default AddOrUpdateShortNumber;
